import * as React from 'react';
import {useState} from 'react';
import Avatar from '@mui/material/Avatar';
import { Formik, useFormik } from 'formik';
import * as Yup from 'yup';
import Button from '@mui/material/Button';
import CssBaseline from '@mui/material/CssBaseline';
import TextField from '@mui/material/TextField';
import FormControlLabel from '@mui/material/FormControlLabel';
import Checkbox from '@mui/material/Checkbox';
import Link from '@mui/material/Link';
import Paper from '@mui/material/Paper';
import Box from '@mui/material/Box';
import Grid from '@mui/material/Grid';
import LockOutlinedIcon from '@mui/icons-material/LockOutlined';
import Typography from '@mui/material/Typography';
import { createTheme, ThemeProvider } from '@mui/material/styles';
import Alert from '@mui/material/Alert';
import { useNavigate } from 'react-router-dom'; 

function Login() {


  const [showAlert, setShowAlert] = useState(false);
  const [alertMessage, setAlertMessage] = useState('');
  const navigate = useNavigate();


 const formik = useFormik({
  initialValues : {
  email : '',
  password : '',
  subscribe : false

  },
 validationSchema: Yup.object ({
  email:Yup.string().required("Email is requried").email("Invalid email address"),
  password:Yup.string().required("Password is required")
 }),
 onSubmit: async (values,{resetForm}) => {
  try {
    let result = await fetch("http://localhost:8000/api/Login", {
      method: 'POST',
      body: JSON.stringify(formik.values),
      headers: {
        "Content-Type": 'application/json',
        "Accept": 'application/json',
      }
    });


    if(result.ok) {
      const { token, role } = await result.json();
    localStorage.setItem('token',token)
    console.log(token);

    if (role === 'admin') {
      const adminResponse = await fetch("http://localhost:8000/api/admin", {
        method: 'GET',
        headers: {
          "Authorization": `Bearer ${token}`
        }
      });

      if (adminResponse.ok) {
       
      } else {
        console.log("Failed to fetch admin data:", adminResponse.statusText);
      }

      navigate('/dashboard'); 
    } else {
      const userResponse = await fetch("http://localhost:8000/api/user1", {
        method: 'GET',
        headers: {
          "Authorization": `Bearer ${token}`
        }
      });

      if (userResponse.ok) {
       
      } else {
        console.log("Failed to fetch user data:", userResponse.statusText);
      }

      navigate('/home'); 
    }


    resetForm();
    } else {
     console.log("Login failed",values.statusText)
     setShowAlert(true);
     setAlertMessage("Login failed. Please check your credentials.");
     }
  } catch (error) {
    console.error('Error:', error);
    setShowAlert(true);
    setAlertMessage("An error occurred while logging in.");
  }
}
});


  

  return (
    <ThemeProvider theme={createTheme()}>
      <Grid container component="main" sx={{ height: '100vh' }}>
        <CssBaseline />
        <Grid
          item
          xs={false}
          sm={4}
          md={7}
          sx={{
            backgroundImage: 'url(https://source.unsplash.com/random?restaurant)',
            backgroundRepeat: 'no-repeat',
            backgroundColor: (theme) =>
              theme.palette.mode === 'light' ? theme.palette.grey[50] : theme.palette.grey[900],
            backgroundSize: 'cover',
            backgroundPosition: 'center',
          }}
        />
        <Grid item xs={12} sm={8} md={5} component={Paper} elevation={6} square>
          <Box
            sx={{
              my: 8,
              mx: 4,
              display: 'flex',
              flexDirection: 'column',
              alignItems: 'center',
            }}
          >
          {showAlert && (
              <Box mb={2}>
                <Alert severity="error" onClose={() => setShowAlert(false)}>{alertMessage}</Alert>
              </Box>
            )}
            <Avatar sx={{ m: 1, bgcolor: 'secondary.main' }}>
              <LockOutlinedIcon />
            </Avatar>
            <Typography component="h1" variant="h5">
              Restaurant Login
            </Typography>
            <Box component="form" noValidate onSubmit={formik.handleSubmit} sx={{ mt: 1 }}>
              <TextField
                margin="normal"
                required
                fullWidth
                id="email"
                label="Email Address"
                name="email"
                autoComplete="email"
                autoFocus
                value = {formik.values.email}
                onChange={formik.handleChange}
                error = {formik.touched.email && Boolean(formik.errors.email)}
                helperText = {formik.touched.email && formik.errors.email}
                
              />
              <TextField
                margin="normal"
                required
                fullWidth
                name="password"
                label="Password"
                type="password"
                id="password"
                autoComplete="current-password"
                value = {formik.values.password}
                onChange = {formik.handleChange}
                error = {formik.touched.password && Boolean(formik.errors.password)}
                helperText = {formik.touched.password && formik.errors.password}
                />
              <FormControlLabel
                control={<Checkbox name="subscribe" onChange={formik.handleChange} checked = {formik.values.subscribe} color="primary" />}
                label="Remember me"
              />
              <Button
                type="submit"
                fullWidth
                variant="contained"
                sx={{ mt: 3, mb: 2 }}
              >
                Login
              </Button>
              <Grid container>
                <Grid item xs>
                  <Link href="Forgot" variant="body2">
                    Forgot password?
                  </Link>
                </Grid>
                <Grid item>
                  <Link href="SignUp" variant="body2">
                    {"Don't have an account? Sign Up"}
                  </Link>
                </Grid>
              </Grid>
            </Box>
          </Box>
        </Grid>
      </Grid>
    </ThemeProvider>
  );
}

export default Login;
